mapper.select:
select  doc_code, doc_name,  start_time, end_time, doc_phone, status, remark,
group_concat(comment,fiest_dept, second_dept) as doc_dept from base_doc_info

    1 = 1
     and doc_name like concat('%', #{docName}, '%')
     and supplier_code = #{supplierCode}
     and( fiest_dept like concat('%', #{fiestDept},'%') or second_dept like concat('%',#{secondDept},'%') or comment like concat('%',#{comment},'%')) 
     and start_time = #{startTime}
     and end_time = #{endTime}
     and doc_phone = #{docPhone}
     and doc_dept = #{docDept}
     and province = #{province}
     and city = #{city}
     and district = #{district}
    and status="Y"
    and remark = #{remark}

group by  doc_code, doc_name, start_time, end_time, doc_phone, status, remark

mapper.update:
update base_doc_info
doc_name = #{docName}, supplier_code = #{supplierCode}, fiest_dept = #{fiestDept}, second_dept = #{secondDept}, start_time = #{startTime}, end_time = #{endTime}, doc_phone = #{docPhone}, doc_dept = #{docDept}, status = #{status}, province = #{province}, city = #{city}, district = #{district}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, comment = #{comment},
where doc_code = #{docCode}
