mapper.update:
update base_service_info
service_code = #{serviceCode}, service_name = #{serviceName}, category_code = #{categoryCode}, category_name = #{categoryName}, alias = #{alias}, servertype_code = #{servertypeCode}, servertype_name = #{servertypeName}, orgin = #{orgin}, status = #{status}, original_code = #{originalCode}, original_name = #{originalName}, clause = #{clause}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where serial_no = #{serialNo}

mapper.select:
select serial_no, service_code , service_name,category_name,servertype_code,servertype_name,status  from base_service_info
1 = 1 AND state = 'Y'
    and status = #{status}
