mapper.update:
update claim_case_appeal_task
appeal_rpt_no = #{appealRptNo}, new_rpt_no = #{newRptNo}, appeal_status = #{appealStatus}, appeal_type = #{appealType}, appeal_reason = #{appealReason}, appeal_sub_reason = #{appealSubReason}, appeal_remark = #{appealRemark}, apply_operator = #{applyOperator}, auditor = #{auditor}, status = #{status}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, dept_code = #{deptCode}, conclusion_remark = #{conclusionRemark},
where task_id = #{taskId}

mapper.select:
SELECT DISTINCT t.task_id, t.appeal_rpt_no, t2.source, t3.name, t3.id_no, t2.claim_type, t4.company_code, t4.company_name, t4.policy_manage_com, t5.pay_amount, t.appeal_status, t.appeal_type, t.appeal_reason, t.appeal_sub_reason, t.appeal_remark , t.conclusion_remark , t.apply_operator , t.auditor , t.status , t.dept_code , t.create_time , t.create_by, t.update_time, t.update_by, t2.batch_no from claim_case_appeal_task t left JOIN claim_case t1 on t1.rpt_no = t.appeal_rpt_no LEFT JOIN claim_batch t2 ON t1.batch_no = t2.batch_no LEFT JOIN claim_case_insured t3 ON t1.rpt_no = t3.rpt_no LEFT JOIN claim_case_policy t4 ON t3.rpt_no = t4.rpt_no LEFT JOIN claim_case_cal t5 ON t4.rpt_no = t5.rpt_no WHERE 1 = 1 AND t.status = 'Y' AND t1.status = 'Y' AND t2.status = 'Y' AND t2.status = 'Y' AND t4.status = 'Y' AND t5.status = 'Y' and t.appeal_status = #{appealStatus} and t.appeal_rpt_no = #{appealRptNo} and t2.source = #{source} and t3.id_no = #{idNo} and t3.name = #{name} and t.auditor = #{auditor} and t.appeal_status in ('01','04') and t.appeal_status in ('02','03')
AND t.update_time >= #{createStartTime}
AND t.update_time <= #{createEndTime}

mapper.insert:
INSERT INTO claim_case ( `rpt_no`, `batch_no`, `filing_no`, `case_status`, `pullout_type`, `pullout_reason`, `pullout_describe`, `end_case_time`, `is_appeal`, `pay_status`, `is_finance_back`, `status`, `create_by`, `create_time`, `update_by`, `update_time` ) ( SELECT #{newRptNo}, `batch_no`, `filing_no`, `case_status`, `pullout_type`, `pullout_reason`, `pullout_describe`, `end_case_time`, `is_appeal`, `pay_status`, `is_finance_back`, `status`, `create_by`, `create_time`, `update_by`, `update_time` FROM claim_case WHERE rpt_no = #{appealRptNo} AND STATUS = 'Y' ) ;
INSERT INTO `claim_case_accept` ( `rpt_no`, `claim_amount`, `acc_date`, `acc_type`, `curr_situation`, `first_ser_illness_date`, `death_date`, `disability_date`, `pre_auth_flag`, `pre_auth_item`, `pre_auth_no`, `material_complete_date`, `receipt_date`, `pri_reason`, `acc_province`, `acc_city`, `acc_district`, `acc_address`, `acc_describe`, `status`, `create_by`, `create_time`, `update_by`, `update_time`, `customer_application_date` ) ( SELECT #{newRptNo}, `claim_amount`, `acc_date`, `acc_type`, `curr_situation`, `first_ser_illness_date`, `death_date`, `disability_date`, `pre_auth_flag`, `pre_auth_item`, `pre_auth_no`, `material_complete_date`, `receipt_date`, `pri_reason`, `acc_province`, `acc_city`, `acc_district`, `acc_address`, `acc_describe`, `status`, `create_by`, `create_time`, `update_by`, `update_time`, `customer_application_date` FROM claim_case_accept WHERE rpt_no = #{appealRptNo} AND STATUS = 'Y' );
INSERT INTO `claim_case_apply_type` ( `rpt_no`, `apply_type`, `status`, `create_by`, `create_time`, `update_by`, `update_time` ) ( SELECT #{newRptNo}, `apply_type`, `status`, `create_by`, `create_time`, `update_by`, `update_time` FROM claim_case_apply_type WHERE rpt_no = #{appealRptNo} AND STATUS = 'Y' );
INSERT INTO `claim_case_bill` ( `rpt_no`, `hospital_code`, `department`, `is_des_hospital`, `acc_type`, `bill_currency`, `bill_amount`, `vis_number`, `treatment_type`, `treatment_start_date`, `treatment_end_date`, `treatment_Days`, `invoice_no`, `bill_no`, `bill_type`, `ss_advance_payment`, `tp_advance_payment`, `is_share_ap`, `trans_serial_no`, `trans_Serial_copay`, `copay`, `is_share_copay`, `hos_discount_amount`, `is_share_dis_amount`, `self_amount`, `part_self_amount`, `unable_amount`, `clinical_diagnosis`, `status`, `create_by`, `create_time`, `update_by`, `update_time` )( SELECT #{newRptNo}, `hospital_code`, `department`, `is_des_hospital`, `acc_type`, `bill_currency`, `bill_amount`, `vis_number`, `treatment_type`, `treatment_start_date`, `treatment_end_date`, `treatment_Days`, `invoice_no`, `bill_no`, `bill_type`, `ss_advance_payment`, `tp_advance_payment`, `is_share_ap`, `trans_serial_no`, `trans_Serial_copay`, `copay`, `is_share_copay`, `hos_discount_amount`, `is_share_dis_amount`, `self_amount`, `part_self_amount`, `unable_amount`, `clinical_diagnosis`, `status`, `create_by`, `create_time`, `bill_id`, `update_time` FROM claim_case_bill WHERE rpt_no = #{appealRptNo} AND STATUS = 'Y' );
INSERT INTO `claim_case_bill_detail` ( `bill_id`, `rpt_no`, `fee_item_code`, `fee_item_name`, `bill_detail_amount`, `self_amount`, `part_self_amount`, `advance_payment`, `bill_detail_copay`, `hos_discount_amount`, `unable_amount`, `vis_number`, `remark`, `status`, `create_by`, `create_time`, `update_by`, `update_time` ) ( SELECT bill_id, #{newRptNo}, `fee_item_code`, `fee_item_name`, `bill_detail_amount`, `self_amount`, `part_self_amount`, `advance_payment`, `bill_detail_copay`, `hos_discount_amount`, `unable_amount`, `vis_number`, `remark`, `status`, `create_by`, `create_time`, `update_by`, `update_time` FROM claim_case_bill_detail WHERE rpt_no = #{appealRptNo} and status = 'Y' );
INSERT INTO `claim_case_bill_diagnosis` ( `rpt_no`, `bill_id`, `icd_code`, `dig_type`, `dig_order`, `status`, `create_by`, `create_time`, `update_by`, `update_time` )( SELECT #{newRptNo}, `bill_id`, `icd_code`, `dig_type`, `dig_order`, `status`, `create_by`, `create_time`, `update_by`, `update_time` FROM claim_case_bill_diagnosis WHERE rpt_no = #{appealRptNo} and status = 'Y' );
INSERT INTO `claim_case_insured` ( `rpt_no`, `insured_no`, `name`, `sex`, `id_type`, `id_no`, `birthday`, `occupation`, `nationality`, `id_start_date`, `id_end_date`, `mobile`, `email`, `phone`, `province`, `city`, `district`, `address`, `status`, `create_by`, `create_time`, `update_by`, `update_time` ) ( SELECT #{newRptNo}, `insured_no`, `name`, `sex`, `id_type`, `id_no`, `birthday`, `occupation`, `nationality`, `id_start_date`, `id_end_date`, `mobile`, `email`, `phone`, `province`, `city`, `district`, `address`, `status`, `create_by`, `create_time`, `update_by`, `update_time` FROM claim_case_insured WHERE rpt_no = #{appealRptNo} and status = 'Y' );
INSERT INTO `claim_case_payee` ( `rpt_no`, `pay_mode`, `relation_ins`, `payee_name`, `payee_sex`, `payee_id_type`, `payee_id_no`, `payee_occupation`, `payee_nationality`, `id_end_date`, `payee_mobile`, `payee_bank`, `acc_attribute`, `acc_no`, `payee_ratio`, `province`, `city`, `district`, `address`, `status`, `create_by`, `create_time`, `update_by`, `update_time`, `currency` )( SELECT #{newRptNo}, `pay_mode`, `relation_ins`, `payee_name`, `payee_sex`, `payee_id_type`, `payee_id_no`, `payee_occupation`, `payee_nationality`, `id_end_date`, `payee_mobile`, `payee_bank`, `acc_attribute`, `acc_no`, `payee_ratio`, `province`, `city`, `district`, `address`, `status`, `create_by`, `create_time`, `update_by`, `update_time`, `currency` FROM claim_case_payee WHERE rpt_no = #{appealRptNo} and status = 'Y' );
INSERT INTO `claim_case_register` ( `rpt_no`, `relation_ins`, `rgt_name`, `rgt_sex`, `rgt_id_type`, `rgt_id_no`, `rgt_birthday`, `rgt_mobile`, `rgt_email`, `rgt_phone`, `rgt_province`, `rgt_city`, `rgt_district`, `rgt_address`, `status`, `create_by`, `create_time`, `update_by`, `update_time` )( select #{newRptNo}, `relation_ins`, `rgt_name`, `rgt_sex`, `rgt_id_type`, `rgt_id_no`, `rgt_birthday`, `rgt_mobile`, `rgt_email`, `rgt_phone`, `rgt_province`, `rgt_city`, `rgt_district`, `rgt_address`, `status`, `create_by`, `create_time`, `update_by`, `update_time` from claim_case_register where rpt_no = #{appealRptNo} and status = 'Y' );
