mapper.update:
 update work_order_accept set status = '02'
where status='01'and business_type='信息需求' and work_order_no in

    #{workOrderNo}

mapper.select:
select t1.work_order_no,t2.channel_code,t2.item_code,t1.policy_no,t1.policy_item_no,t1.risk_code,t1.insured_name,t1.holder_name,t1.accept_time,t1.accept_by,t1.modify_time,t1.vip_flag,t2.priority_level,t2.organ_code,t3.status
from work_order_accept t1
left join  accept_detail_info t2  on t1.work_order_no=t2.work_order_no
left join person_info c on t1.insured_no=c.person_id
left join collaborative_from t3  on t1.work_order_no=t3.work_order_no
where
t1.work_order_no = t2.work_order_no
and t1.business_type='投诉'  or t1.business_type='信息需求'
and t1.status='02'
and t3.status='01'

     and t2.channel_code = #{channelCode}
     and t2.item_code = #{itemCode}
     and t1.accept_by = #{acceptBy}
     and t1.accept_time = #{acceptTime}
     and t1.accept_time between #{acceptTimeStart} and DATE_ADD(#{acceptTimeEnd},INTERVAL 1 DAY)
     and t1.modify_by = #{modifyBy}
     and t1.modify_time between #{modifyTimeStart} and DATE_ADD(#{modifyTimeEnd},INTERVAL 1 DAY)
     and t1.work_order_no= #{workOrderNo}
     and t1.policy_no = #{policyNo}
     and t1.policy_item_no = #{policyItemNo}
     and t1.insured_name like concat('%', #{insuredName}, '%')
     and t1.holder_name like concat('%', #{holderName}, '%')
     and t1.vip_flag = #{vipFlag}
     and t1.organ_code = #{organCode}
     and t2.priority_level = #{priorityLevel}
     and t1.status = #{status}
     and c.id_number = #{idNumber}
     and c.mobile_phone = #{mobilePhone}
