mapper.select:

where 1=1 and not exists (SELECT 1 FROM quality_inspection_accept qia WHERE qia.work_order_no=woa.work_order_no)

    and woa.business_type IN (

        #{businessType}

    )


    and DATE_FORMAT(woa.end_date,'%Y-%m-%d') >= #{endCaseStartTime}


    and DATE_FORMAT(woa.end_date,'%Y-%m-%d') <= #{endCaseEndTime}

 and adi.item_code = #{itemCode}
 and adi.channel_code = #{channelCode}
 and woa.accept_by = #{acceptBy}

    and DATE_FORMAT(woa.accept_time,'%Y-%m-%d') >= #{acceptStartDate}


    and DATE_FORMAT(woa.accept_time,'%Y-%m-%d') <= #{acceptEndDate}

 and woa.update_by = #{updateBy}

    and DATE_FORMAT(woa.update_time,'%Y-%m-%d') >= #{updateStartDate}


    and DATE_FORMAT(woa.update_time,'%Y-%m-%d') <= #{updateEndDate}

 and woa.work_order_no = #{workOrderNo}
 and woa.policy_no = #{policyNo}
 and woa.policy_item_no = #{policyItemNo}
 and woa.holder_name = #{holderName}
 and woa.insured_name = #{insuredName}
 and pi.id_number = #{idNumber}
 and pi.mobile_phone = #{mobilePhone}
 and woa.organ_code = #{organCode}

    and DATE_FORMAT(adi.prop18,'%Y-%m-%d') >= #{appointmentStartDate}


    and DATE_FORMAT(adi.prop18,'%Y-%m-%d') <= #{appointmentEndDate}

 and adi.priority_level = #{priorityLevel}
 and woa.vip_flag = #{vipFlag}
 and woa.status =


where 1=1 and not exists (SELECT 1 FROM quality_inspection_accept qia WHERE qia.work_order_no=woa.work_order_no)
and woa.status ='02'
and woa.work_order_no=#{workOrderNo}


where 1=1 and not exists (SELECT 1 FROM quality_inspection_accept qia WHERE qia.work_order_no=woa.work_order_no)
and woa.status !='02'
and woa.work_order_no=#{workOrderNo}


where 1=1


    and DATE_FORMAT(woa.end_date,'%Y-%m-%d') >= #{endCaseStartTime}



    and DATE_FORMAT(woa.end_date,'%Y-%m-%d') <= #{endCaseEndTime}


 and woa.organ_code = #{organCode}

 and qia.status = #{acceptStatus}

 and adi.item_code = #{serviceItem}

 and qia.updated_by = #{updateBy}

select c.work_order_no workOrderNo, b.item_code itemCode, a.modify_by modifyBy, c.updated_by updatedBy, a.business_type businessType from quality_inspection_accept c left join accept_detail_info b on c.work_order_no = b.work_order_no left join work_order_accept a on a.work_order_no = c.work_order_no
and end_date >= #{firstEndCaseStartTime}
and end_date <= #{firstEndCaseEndTime}
and c.work_order_no = #{workOrderNo}
and b.item_code = #{serviceItemCode}
and c.status = '02'

mapper.update:
UPDATE quality_inspection_accept
SET
status = #{acceptStatus},
updated_by = #{updateBy},
updated_time = #{updateTime}
WHERE status='01' and
work_order_no IN (

    #{workOrderNo}

)

mapper.select:
select woa.update_by,count(*) as count,GROUP_CONCAT(work_order_no) as work_order_nos from work_order_accept woa where 1=1
 and woa.updated_by = #{updatedBy}
 and woa.status = #{status}
 and woa.work_order_no = #{workOrderNo}

    and DATE_FORMAT(woa.end_date,'%Y-%m-%d') >= #{endCaseStartTime}


    and DATE_FORMAT(woa.end_date,'%Y-%m-%d') <= #{endCaseEndTime}

group by woa.update_by;
